            if (this.disabled === true) {
                return;
            }
            var tags = data.obj.parent.selectedItems.select(
                function(i) {
                    if (i.cls.search(/tag/) >= 0) {
                        return true;
                    }
                    return false;
                });
            tags = tags.map(function(i) {return i.text;});
