Ext.extend(Ext.testia.CSVImportDialog, Ext.LayoutDialog, {
    dForm: undefined,
    uploadUrl: undefined,
    callback: undefined,

    closeDialog: function(b) {
        if (b === 'ok') {
            this.upload({
                callback: function() {
                    if (this.callback) {
                        this.callback();
                    }
                    this.destroy(true);
                },
                scope: this
            });
        } else {
            this.destroy(true);
        }
    },

    upload: function(options) {
        var cb = function() {
            if (options.callback) {
                options.callback.call(options.scope || this);
            }
        };
        this.dForm.submit({
            url: this.uploadUrl,
            params: '_method=put',
            enctype:'multipart/form-data',
            success: cb,
            failure: cb
        });
    }
});
